import React, { Suspense, lazy, useMemo } from 'react';
import { Platform, View, Text, StyleSheet, SafeAreaView } from 'react-native';
import InstantSEO from '@/components/seo/LazyInstantSEO';
import {useIsFocused} from "@react-navigation/native";
import { useThemedColors } from '@/hooks/useTheme';

const isWeb = Platform.OS === 'web';
const isClient = typeof window !== 'undefined';
const ListTravel = isWeb && isClient
  ? lazy(() => import('@/components/listTravel/ListTravelBase'))
  : require('@/components/listTravel/ListTravelBase').default;

export default function MeTravelScreen() {
    const { buildCanonicalUrl, buildOgImageUrl } = require('@/utils/seo');
    const isFocused = useIsFocused();
    //  canonical     head
    const canonical = useMemo(() => buildCanonicalUrl('/metravel'), []);
    const colors = useThemedColors();
    const styles = useMemo(() => createStyles(colors), [colors]);

    return (
        <>
            {isFocused && (
            <InstantSEO
                headKey="metravel" //     
                title="  | Metravel"
                description="        Metravel."
                canonical={canonical}
                image={buildOgImageUrl('/og-preview.jpg')}
                ogType="website"
                robots="noindex, nofollow"
            />
            )}
            <SafeAreaView style={styles.container}>
                <Suspense
                    fallback={
                        <View style={styles.loading}>
                            <Text style={styles.loadingText}></Text>
                        </View>
                    }
                >
                    <ListTravel />
                </Suspense>
            </SafeAreaView>
        </>
    );
}

const createStyles = (colors: ReturnType<typeof useThemedColors>) => StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: colors.mutedBackground,
    },
    loading: {
        padding: 16,
    },
    loadingText: {
        color: colors.text,
    },
});

