import { useCallback, useEffect, useMemo, useState } from 'react';
import { Platform } from 'react-native';
import { usePathname } from 'expo-router';
import { useSafeAreaInsets } from 'react-native-safe-area-context';

import FiltersPanel from '@/components/MapPage/FiltersPanel';
import { CoordinateConverter } from '@/utils/coordinateConverter';
import { useDebouncedValue } from '@/hooks/useDebouncedValue';
import { useRouteStoreAdapter } from '@/hooks/useRouteStoreAdapter';
import { useThemedColors } from '@/hooks/useTheme';
import { getStyles } from '@/app/(tabs)/map.styles';
import type { MapUiApi } from '@/src/types/mapUi';
import type { TravelCoords } from '@/src/types/types';
import { logMessage } from '@/src/utils/logger';

import {
  loadMapFilterValues,
  saveMapFilterValues,
  type StorageLike,
} from '@/src/utils/mapFiltersStorage';

//    
import {
  useMapCoordinates,
  useMapFilters,
  useMapTravels,
  useMapPanelState,
  useMapResponsive,
} from '@/hooks/map';
import { getSiteBaseUrl, buildCanonicalUrl } from '@/utils/seo';

const HEADER_HEIGHT_WEB = 88;

/**
 *    .
 *       API  .
 */
export function useMapScreenController() {
  const pathname = usePathname();
  const insets = useSafeAreaInsets();
  const themedColors = useThemedColors();

  const webStorage = useMemo<StorageLike | null>(() => {
    if (Platform.OS !== 'web') return null;
    if (typeof localStorage === 'undefined') return null;
    return localStorage;
  }, []);

  // Responsive
  const { isMobile, width } = useMapResponsive();

  // Panel state
  const {
    isFocused,
    mapReady,
    rightPanelTab,
    rightPanelVisible,
    selectFiltersTab,
    selectTravelsTab,
    openRightPanel,
    closeRightPanel,
    panelStyle,
    overlayStyle,
    filtersTabRef,
    panelRef,
  } = useMapPanelState({ isMobile });

  // Map API reference
  const [mapUiApi, setMapUiApi] = useState<MapUiApi | null>(null);

  // Coordinates
  const { coordinates } = useMapCoordinates();

  // Filters
  const {
    filters,
    filterValues,
    handleFilterChangeForPanel,
    resetFilters: resetFiltersBase,
  } = useMapFilters();

  // Route store
  const routeStore = useRouteStoreAdapter();
  const {
    mode,
    setMode,
    transportMode,
    setTransportMode,
    routePoints,
    startAddress,
    endAddress,
    routeDistance,
    fullRouteCoords,
    setRoutePoints,
    setRouteDistance,
    setFullRouteCoords,
    handleClearRoute,
    handleAddressSelect,
    points: routeStorePoints,
    isBuilding: routingLoading,
    error: routingError,
  } = routeStore;

  const didRestorePersistedUiStateRef = useMemo(() => ({ current: false }), []);

  useEffect(() => {
    if (!webStorage) return;
    if (didRestorePersistedUiStateRef.current) return;

    didRestorePersistedUiStateRef.current = true;

    const persisted = loadMapFilterValues(webStorage);
    if (persisted.lastMode) setMode(persisted.lastMode);
    if (persisted.transportMode) setTransportMode(persisted.transportMode);
  }, [setMode, setTransportMode, webStorage, didRestorePersistedUiStateRef]);

  useEffect(() => {
    if (!webStorage) return;
    saveMapFilterValues(webStorage, {
      ...filterValues,
      lastMode: mode,
      transportMode,
    });
  }, [filterValues, mode, transportMode, webStorage]);

  // Route hint state
  const [routeHintDismissed, setRouteHintDismissed] = useState(false);
  const setRouteHintDismissedTrue = useCallback(() => setRouteHintDismissed(true), []);

  // Debounced values for stable queries
  const debounceTime = isMobile ? 300 : 500;
  const debouncedCoordinates = useDebouncedValue(coordinates, debounceTime);
  const debouncedFilterValues = useDebouncedValue(filterValues, 300);

  // Travels data
  const {
    allTravelsData,
    filteredTravelsData: travelsData,
    isLoading: loading,
    isFetching,
    isPlaceholderData,
    isError: mapError,
    error: mapErrorDetails,
    refetch: refetchMapData,
  } = useMapTravels({
    coordinates: debouncedCoordinates,
    filterValues: debouncedFilterValues,
    filters,
    mode,
    fullRouteCoords,
    isFocused,
  });

  // Invalidate query callback
  const invalidateTravelsQuery = useCallback(() => {
    refetchMapData();
  }, [refetchMapData]);

  // Reset filters and route
  const resetFilters = useCallback(() => {
    resetFiltersBase();
    routeStore.clearRoute();
    setMode('radius');
  }, [resetFiltersBase, routeStore, setMode]);

  // Handle map click for route building
  const handleMapClick = useCallback(
    (lng: number, lat: number) => {
      if (
        !Number.isFinite(lng) ||
        !Number.isFinite(lat) ||
        lng < -180 ||
        lng > 180 ||
        lat < -90 ||
        lat > 90
      ) {
        logMessage('[map] Invalid coordinates received', 'warning', { scope: 'map', lng, lat });
        return;
      }

      if (mode === 'route' && routePoints.length < 2) {
        const newPoint: [number, number] = [lng, lat];
        setRoutePoints([...routePoints, newPoint]);
      }
    },
    [mode, routePoints, setRoutePoints]
  );

  // Build route to travel item
  const buildRouteTo = useCallback(
    (item: TravelCoords) => {
      if (!item?.coord) return;
      const rawCoordStr = String(item.coord);
      const parsed = CoordinateConverter.fromLooseString(rawCoordStr);
      const coordStr = parsed ? CoordinateConverter.toString(parsed) : rawCoordStr;

      try {
        mapUiApi?.focusOnCoord?.(coordStr, { zoom: 14 });
      } catch {
        // noop
      }

      try {
        // Open popup after the map starts moving so user immediately sees the exact point.
        setTimeout(() => {
          try {
            mapUiApi?.openPopupForCoord?.(coordStr);
          } catch {
            // noop
          }
        }, 420);
      } catch {
        // noop
      }
    },
    [mapUiApi]
  );

  const centerOnUser = useCallback(() => {
    try {
      mapUiApi?.centerOnUser?.();
    } catch {
      // noop
    }
  }, [mapUiApi]);

  // Build route from store points
  const handleBuildRoute = useCallback(() => {
    if (routeStorePoints.length >= 2) {
      const points: [number, number][] = routeStorePoints.map((p) => [
        p.coordinates.lng,
        p.coordinates.lat,
      ]);
      setRoutePoints(points);
      return;
    }
    logMessage('[map] Not enough route points to build route', 'warning', {
      scope: 'map',
      step: 'buildRoute',
      pointsLength: routeStorePoints.length,
    });
  }, [routeStorePoints, setRoutePoints]);

  // Canonical URL
  const canonical = buildCanonicalUrl(pathname || '/map');

  // Styles
  const headerOffset = Platform.OS === 'web' ? HEADER_HEIGHT_WEB : 0;
  const styles = useMemo(
    () => getStyles(isMobile, insets.top, headerOffset, width, themedColors),
    [isMobile, insets.top, headerOffset, width, themedColors]
  );

  // Map panel coordinates (with safe defaults)
  const mapPanelCoordinates = useMemo(() => {
    if (!coordinates || !Number.isFinite(coordinates.latitude) || !Number.isFinite(coordinates.longitude)) {
      // Default to Minsk coordinates
      return { latitude: 53.9006, longitude: 27.559 };
    }
    return { latitude: coordinates.latitude, longitude: coordinates.longitude };
  }, [coordinates]);

  // Map panel props
  const mapPanelProps = useMemo(
    () => ({
      travelsData,
      coordinates: mapPanelCoordinates,
      routePoints,
      mode,
      transportMode,
      radius: filterValues.radius,
      setRoutePoints,
      setRouteDistance,
      setFullRouteCoords,
      onMapClick: handleMapClick,
      onMapUiApiReady: setMapUiApi,
    }),
    [
      travelsData,
      mapPanelCoordinates,
      routePoints,
      mode,
      transportMode,
      filterValues.radius,
      setRoutePoints,
      setRouteDistance,
      setFullRouteCoords,
      handleMapClick,
    ]
  );

  // Filters panel props
  const filtersPanelProps = useMemo(() => {
    const props = {
      filters: {
        categories: filters.categories
          .filter((c) => c && c.name)
          .map((c) => ({
            id: Number(c.id) || 0,
            name: String(c.name || '').trim(),
          }))
          .filter((c) => c.name),
        radius: filters.radius.map((r) => ({ id: r.id, name: r.name })),
        address: filters.address,
      },
      filterValue: filterValues,
      onFilterChange: handleFilterChangeForPanel,
      resetFilters,
      travelsData: allTravelsData,
      filteredTravelsData: travelsData,
      isMobile,
      mode,
      setMode,
      transportMode,
      setTransportMode,
      startAddress,
      endAddress,
      routeDistance,
      routePoints: routeStorePoints,
      onRemoveRoutePoint: routeStore.removePoint,
      onClearRoute: handleClearRoute,
      swapStartEnd: routeStore.swapStartEnd,
      routeHintDismissed,
      onRouteHintDismiss: setRouteHintDismissedTrue,
      onAddressSelect: handleAddressSelect,
      routingLoading,
      routingError,
      onBuildRoute: handleBuildRoute,
      mapUiApi,
      closeMenu: closeRightPanel,
      userLocation: coordinates,
      onPlaceSelect: buildRouteTo,
      onOpenList: selectTravelsTab,
    };

    return { Component: FiltersPanel, props };
  }, [
    filters,
    filterValues,
    handleFilterChangeForPanel,
    resetFilters,
    allTravelsData,
    travelsData,
    isMobile,
    mode,
    setMode,
    transportMode,
    setTransportMode,
    startAddress,
    endAddress,
    routeDistance,
    routeStorePoints,
    routeStore.removePoint,
    handleClearRoute,
    routeStore.swapStartEnd,
    routeHintDismissed,
    setRouteHintDismissedTrue,
    handleAddressSelect,
    routingLoading,
    routingError,
    handleBuildRoute,
    mapUiApi,
    closeRightPanel,
    coordinates,
    buildRouteTo,
    selectTravelsTab,
  ]);

  return {
    // SEO
    canonical,

    // State
    isFocused,
    isMobile,
    themedColors,
    styles,
    mapReady,

    // Map
    mapPanelProps,

    // Panel
    rightPanelTab,
    rightPanelVisible,
    selectFiltersTab,
    selectTravelsTab,
    openRightPanel,
    closeRightPanel,
    panelStyle,
    overlayStyle,

    // Filters
    filtersPanelProps,

    // Travels data
    travelsData,
    allTravelsData,
    loading,
    isFetching,
    isPlaceholderData,
    mapError,
    mapErrorDetails,
    refetchMapData,
    invalidateTravelsQuery,

    // Route actions
    buildRouteTo,
    centerOnUser,

    // Refs
    filtersTabRef,
    panelRef,

    // Additional data for mobile layout
    coordinates,
    transportMode,
  };
}

const CACHE_VERSION = 'v1.0.0';
const STATIC_CACHE = `metravel-static-${CACHE_VERSION}`;
const DYNAMIC_CACHE = `metravel-dynamic-${CACHE_VERSION}`;
const IMAGE_CACHE = `metravel-images-${CACHE_VERSION}`;

const STATIC_ASSETS = [
  '/',
  '/offline.html',
  '/manifest.json',
  '/favicon.ico',
  '/icon.svg',
];

const MAX_CACHE_SIZE = 100;
const MAX_IMAGE_CACHE_SIZE = 50;
const CACHE_EXPIRATION_TIME = 7 * 24 * 60 * 60 * 1000; // 7 days

self.addEventListener('install', (event) => {
  event.waitUntil(
    caches.open(STATIC_CACHE).then((cache) => {
      return cache.addAll(STATIC_ASSETS.filter(asset => asset !== '/offline.html'));
    }).catch(() => {
      console.log('SW: Static cache install failed');
    })
  );
  self.skipWaiting();
});

self.addEventListener('activate', (event) => {
  event.waitUntil(
    caches.keys().then((cacheNames) => {
      return Promise.all(
        cacheNames
          .filter((name) => name.startsWith('metravel-') && name !== STATIC_CACHE && name !== DYNAMIC_CACHE && name !== IMAGE_CACHE)
          .map((name) => caches.delete(name))
      );
    })
  );
  self.clients.claim();
});

self.addEventListener('fetch', (event) => {
  const { request } = event;
  const url = new URL(request.url);

  if (request.method !== 'GET') return;

  if (url.pathname.startsWith('/api/')) {
    event.respondWith(networkFirst(request));
    return;
  }

  if (request.destination === 'image') {
    event.respondWith(cacheFirst(request, IMAGE_CACHE, MAX_IMAGE_CACHE_SIZE));
    return;
  }

  if (
    request.destination === 'script' ||
    request.destination === 'style' ||
    request.destination === 'font'
  ) {
    event.respondWith(cacheFirst(request, STATIC_CACHE));
    return;
  }

  event.respondWith(networkFirst(request));
});

async function cacheFirst(request, cacheName = DYNAMIC_CACHE, maxSize = MAX_CACHE_SIZE) {
  try {
    const cache = await caches.open(cacheName);
    const cached = await cache.match(request);

    if (cached) {
      const cacheDate = cached.headers.get('sw-cache-date');
      if (cacheDate) {
        const age = Date.now() - parseInt(cacheDate, 10);
        if (age > CACHE_EXPIRATION_TIME) {
          cache.delete(request);
        } else {
          return cached;
        }
      } else {
        return cached;
      }
    }

    const response = await fetch(request);

    if (response && response.status === 200) {
      const responseToCache = response.clone();
      const headers = new Headers(responseToCache.headers);
      headers.append('sw-cache-date', Date.now().toString());

      const blob = await responseToCache.blob();
      const cachedResponse = new Response(blob, {
        status: responseToCache.status,
        statusText: responseToCache.statusText,
        headers: headers,
      });

      cache.put(request, cachedResponse);

      limitCacheSize(cacheName, maxSize);
    }

    return response;
  } catch (error) {
    const cached = await caches.match(request);
    return cached || new Response('Offline', { status: 503 });
  }
}

async function networkFirst(request) {
  try {
    const response = await fetch(request);

    if (response && response.status === 200) {
      const cache = await caches.open(DYNAMIC_CACHE);
      cache.put(request, response.clone());
      limitCacheSize(DYNAMIC_CACHE, MAX_CACHE_SIZE);
    }

    return response;
  } catch (error) {
    const cached = await caches.match(request);
    if (cached) return cached;

    if (request.destination === 'document') {
      return caches.match('/offline.html') || new Response('Offline', { status: 503 });
    }

    return new Response('Network error', { status: 503 });
  }
}

async function limitCacheSize(cacheName, maxSize) {
  try {
    const cache = await caches.open(cacheName);
    const keys = await cache.keys();

    if (keys.length > maxSize) {
      await cache.delete(keys[0]);
      limitCacheSize(cacheName, maxSize);
    }
  } catch (error) {
    console.log('SW: Cache size limit error', error);
  }
}

#!/usr/bin/env node

/**
 * Design System Import Migration Script
 * 
 *      -
 *      @/constants/designSystem
 * 
 * Usage:
 *   node scripts/migrate-design-imports.js [--dry-run] [--path=<path>]
 * 
 * Options:
 *   --dry-run      
 *   --path           (default: components/)
 */

const fs = require('fs');
const path = require('path');

//  
const DEPRECATED_IMPORTS = {
  designTokens: {
    from: /@\/constants\/designTokens/g,
    to: '@/constants/designSystem',
    mappings: {
      'designTokens.colors': 'DESIGN_TOKENS.colors',
      'designTokens.spacing': 'DESIGN_TOKENS.spacing',
      'designTokens.typography': 'DESIGN_TOKENS.typography',
      'designTokens.radius': 'DESIGN_TOKENS.radii',
      'designTokens.borderRadius': 'DESIGN_TOKENS.radii',
      'designTokens.shadows': 'DESIGN_TOKENS.shadows',
    }
  },
  modernRedesign: {
    from: /@\/styles\/modernRedesign/g,
    to: '@/constants/designSystem',
    mappings: {
      'MODERN_COLORS': 'DESIGN_TOKENS.colors',
      'MODERN_SPACING': 'DESIGN_TOKENS.spacing',
      'MODERN_RADII': 'DESIGN_TOKENS.radii',
      'MODERN_SHADOWS': 'DESIGN_TOKENS.shadows',
      'MODERN_TYPOGRAPHY': 'DESIGN_TOKENS.typography',
    }
  },
  airyColors: {
    from: /@\/constants\/airyColors/g,
    to: '@/constants/designSystem',
    mappings: {
      'AIRY_COLORS': 'DESIGN_TOKENS.colors',
      'AIRY_SHADOWS': 'DESIGN_TOKENS.shadowsNative',
    }
  },
  Colors: {
    from: /@\/constants\/Colors/g,
    to: '@/constants/designSystem',
    mappings: {
      'Colors.light': 'DESIGN_TOKENS.colors',
      'Colors.dark': 'DESIGN_TOKENS.colors',
    }
  }
};

// 
const stats = {
  filesProcessed: 0,
  filesModified: 0,
  importsReplaced: 0,
  usagesReplaced: 0,
  errors: 0,
};

function migrateFile(filePath, dryRun = false) {
  try {
    const content = fs.readFileSync(filePath, 'utf8');
    let modified = content;
    let fileModified = false;

    //  1:  
    Object.entries(DEPRECATED_IMPORTS).forEach(([name, config]) => {
      if (config.from.test(content)) {
        console.log(`   Found ${name} import`);
        modified = modified.replace(config.from, config.to);
        fileModified = true;
        stats.importsReplaced++;
      }
    });

    //  2:  
    Object.entries(DEPRECATED_IMPORTS).forEach(([name, config]) => {
      Object.entries(config.mappings).forEach(([oldUsage, newUsage]) => {
        const oldPattern = new RegExp(oldUsage.replace(/\./g, '\\.'), 'g');
        if (oldPattern.test(modified)) {
          const matches = modified.match(oldPattern);
          if (matches) {
            console.log(`     Replacing ${matches.length}x: ${oldUsage}  ${newUsage}`);
            modified = modified.replace(oldPattern, newUsage);
            stats.usagesReplaced += matches.length;
            fileModified = true;
          }
        }
      });
    });

    //  
    if (fileModified) {
      stats.filesModified++;
      if (!dryRun) {
        fs.writeFileSync(filePath, modified, 'utf8');
        console.log(`   Updated: ${filePath}`);
      } else {
        console.log(`   Would update: ${filePath}`);
      }
    }

    stats.filesProcessed++;
  } catch (error) {
    console.error(`   Error processing ${filePath}:`, error.message);
    stats.errors++;
  }
}

function walkDirectory(dir, callback, extensions = ['.tsx', '.ts']) {
  const files = fs.readdirSync(dir);

  files.forEach(file => {
    const filePath = path.join(dir, file);
    const stat = fs.statSync(filePath);

    if (stat.isDirectory()) {
      //  node_modules, dist, build  ..
      if (!['node_modules', 'dist', 'build', '.next', '.expo'].includes(file)) {
        walkDirectory(filePath, callback, extensions);
      }
    } else if (stat.isFile()) {
      const ext = path.extname(file);
      if (extensions.includes(ext)) {
        callback(filePath);
      }
    }
  });
}

function main() {
  const args = process.argv.slice(2);
  const dryRun = args.includes('--dry-run');
  const pathArg = args.find(arg => arg.startsWith('--path='));
  const targetPath = pathArg 
    ? pathArg.split('=')[1] 
    : path.join(__dirname, '../components');

  console.log(' Design System Migration Tool\n');
  console.log(`Target: ${targetPath}`);
  console.log(`Mode: ${dryRun ? ' DRY RUN' : '  WRITE'}\n`);

  const startTime = Date.now();

  if (fs.statSync(targetPath).isDirectory()) {
    walkDirectory(targetPath, (filePath) => {
      console.log(`\n Processing: ${path.relative(process.cwd(), filePath)}`);
      migrateFile(filePath, dryRun);
    });
  } else {
    console.log(`\n Processing: ${path.relative(process.cwd(), targetPath)}`);
    migrateFile(targetPath, dryRun);
  }

  const duration = ((Date.now() - startTime) / 1000).toFixed(2);

  console.log('\n' + '='.repeat(60));
  console.log(' Migration Summary');
  console.log('='.repeat(60));
  console.log(`Files processed:     ${stats.filesProcessed}`);
  console.log(`Files modified:      ${stats.filesModified}`);
  console.log(`Imports replaced:    ${stats.importsReplaced}`);
  console.log(`Usages replaced:     ${stats.usagesReplaced}`);
  console.log(`Errors:              ${stats.errors}`);
  console.log(`Duration:            ${duration}s`);
  console.log('='.repeat(60));

  if (dryRun) {
    console.log('\n This was a dry run. Run without --dry-run to apply changes.');
  } else {
    console.log('\n Migration complete! Run tests to verify changes.');
    console.log('   npm run test:run');
    console.log('   npm run lint');
  }
}

if (require.main === module) {
  main();
}

module.exports = { migrateFile, walkDirectory };

#!/usr/bin/env node

/**
 * Lighthouse performance testing script for travel pages
 * Tests before and after optimization changes
 */

const lighthouse = require('lighthouse');
const chromeLauncher = require('chrome-launcher');
const fs = require('fs');
const path = require('path');

const TRAVEL_URL = process.env.LIGHTHOUSE_URL || 'https://metravel.by/travels/tropa-vedm-harzer-hexenstieg-kak-proiti-marshrut-i-kak-eto-vygliadit-na-samom-dele';
const OUTPUT_DIR = path.join(__dirname, '../lighthouse-reports');

// Ensure output directory exists
if (!fs.existsSync(OUTPUT_DIR)) {
  fs.mkdirSync(OUTPUT_DIR, { recursive: true });
}

const timestamp = new Date().toISOString().replace(/[:.]/g, '-').slice(0, -5);

const mobileConfig = {
  extends: 'lighthouse:default',
  settings: {
    formFactor: 'mobile',
    throttling: {
      rttMs: 150,
      throughputKbps: 1638.4,
      cpuSlowdownMultiplier: 4,
    },
    screenEmulation: {
      mobile: true,
      width: 412,
      height: 823,
      deviceScaleFactor: 2.625,
      disabled: false,
    },
    emulatedUserAgent: 'Mozilla/5.0 (Linux; Android 11; Pixel 5) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Mobile Safari/537.36',
  },
};

const desktopConfig = {
  extends: 'lighthouse:default',
  settings: {
    formFactor: 'desktop',
    throttling: {
      rttMs: 40,
      throughputKbps: 10240,
      cpuSlowdownMultiplier: 1,
    },
    screenEmulation: {
      mobile: false,
      width: 1350,
      height: 940,
      deviceScaleFactor: 1,
      disabled: false,
    },
    emulatedUserAgent: 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36',
  },
};

async function runLighthouse(url, config, formFactor) {
  const chrome = await chromeLauncher.launch({
    chromeFlags: ['--headless', '--disable-gpu', '--no-sandbox'],
  });

  const options = {
    logLevel: 'info',
    output: 'html',
    onlyCategories: ['performance', 'accessibility', 'best-practices', 'seo'],
    port: chrome.port,
  };

  const runnerResult = await lighthouse(url, options, config);

  await chrome.kill();

  return runnerResult;
}

function extractMetrics(lhr) {
  const { categories, audits } = lhr;

  return {
    performance: Math.round(categories.performance.score * 100),
    accessibility: Math.round(categories.accessibility.score * 100),
    bestPractices: Math.round(categories['best-practices'].score * 100),
    seo: Math.round(categories.seo.score * 100),
    metrics: {
      fcp: audits['first-contentful-paint'].numericValue,
      lcp: audits['largest-contentful-paint'].numericValue,
      tbt: audits['total-blocking-time'].numericValue,
      cls: audits['cumulative-layout-shift'].numericValue,
      si: audits['speed-index'].numericValue,
    },
  };
}

function formatMetric(value, unit = 'ms') {
  if (unit === 'ms') {
    return `${Math.round(value)}ms`;
  }
  return value.toFixed(3);
}

function getScoreColor(score) {
  if (score >= 90) return '';
  if (score >= 50) return '';
  return '';
}

function printResults(formFactor, results) {
  console.log(`\n${'='.repeat(60)}`);
  console.log(` ${formFactor.toUpperCase()} PERFORMANCE REPORT`);
  console.log(`${'='.repeat(60)}\n`);

  console.log('SCORES:');
  console.log(`  Performance:     ${getScoreColor(results.performance)} ${results.performance}/100`);
  console.log(`  Accessibility:   ${getScoreColor(results.accessibility)} ${results.accessibility}/100`);
  console.log(`  Best Practices:  ${getScoreColor(results.bestPractices)} ${results.bestPractices}/100`);
  console.log(`  SEO:             ${getScoreColor(results.seo)} ${results.seo}/100\n`);

  console.log('CORE WEB VITALS:');
  console.log(`  FCP (First Contentful Paint):     ${formatMetric(results.metrics.fcp)}`);
  console.log(`  LCP (Largest Contentful Paint):   ${formatMetric(results.metrics.lcp)}`);
  console.log(`  TBT (Total Blocking Time):        ${formatMetric(results.metrics.tbt)}`);
  console.log(`  CLS (Cumulative Layout Shift):    ${formatMetric(results.metrics.cls, 'score')}`);
  console.log(`  SI (Speed Index):                 ${formatMetric(results.metrics.si)}`);
}

function saveComparison(mobileResults, desktopResults) {
  const report = {
    timestamp: new Date().toISOString(),
    url: TRAVEL_URL,
    mobile: mobileResults,
    desktop: desktopResults,
  };

  const filename = path.join(OUTPUT_DIR, `performance-${timestamp}.json`);
  fs.writeFileSync(filename, JSON.stringify(report, null, 2));
  console.log(`\n Detailed report saved to: ${filename}`);
}

async function main() {
  console.log(`\n Testing Travel Page Performance`);
  console.log(` URL: ${TRAVEL_URL}\n`);

  console.log(' Running Mobile Lighthouse audit...');
  const mobileResult = await runLighthouse(TRAVEL_URL, mobileConfig, 'mobile');
  const mobileMetrics = extractMetrics(mobileResult.lhr);

  // Save mobile HTML report
  const mobileHtmlPath = path.join(OUTPUT_DIR, `mobile-${timestamp}.html`);
  fs.writeFileSync(mobileHtmlPath, mobileResult.report);
  console.log(` Mobile HTML report: ${mobileHtmlPath}`);

  printResults('Mobile', mobileMetrics);

  console.log('\n Running Desktop Lighthouse audit...');
  const desktopResult = await runLighthouse(TRAVEL_URL, desktopConfig, 'desktop');
  const desktopMetrics = extractMetrics(desktopResult.lhr);

  // Save desktop HTML report
  const desktopHtmlPath = path.join(OUTPUT_DIR, `desktop-${timestamp}.html`);
  fs.writeFileSync(desktopHtmlPath, desktopResult.report);
  console.log(` Desktop HTML report: ${desktopHtmlPath}`);

  printResults('Desktop', desktopMetrics);

  saveComparison(mobileMetrics, desktopMetrics);

  console.log(`\n${'='.repeat(60)}`);
  console.log(' Testing Complete!');
  console.log(`${'='.repeat(60)}\n`);

  // Summary
  const allGreen = 
    mobileMetrics.performance >= 90 &&
    desktopMetrics.performance >= 90 &&
    mobileMetrics.accessibility >= 90 &&
    desktopMetrics.accessibility >= 90;

  if (allGreen) {
    console.log(' EXCELLENT! All scores are in the green zone (90+)!');
  } else {
    console.log('  Some scores need improvement to reach 90+');

    const improvements = [];
    if (mobileMetrics.performance < 90) improvements.push('Mobile Performance');
    if (desktopMetrics.performance < 90) improvements.push('Desktop Performance');
    if (mobileMetrics.accessibility < 90) improvements.push('Mobile Accessibility');
    if (desktopMetrics.accessibility < 90) improvements.push('Desktop Accessibility');

    console.log(`   Focus areas: ${improvements.join(', ')}`);
  }

  process.exit(allGreen ? 0 : 1);
}

main().catch((error) => {
  console.error(' Error running Lighthouse:', error);
  process.exit(1);
});

#!/usr/bin/env node

/**
 * Design System Verification Script
 * 
 *       -
 *      
 * 
 * Usage:
 *   node scripts/verify-design-system.js
 */

const fs = require('fs');
const path = require('path');

//     
const DEPRECATED_PATTERNS = [
  {
    name: 'designTokens',
    pattern: /@\/constants\/designTokens/,
    severity: 'warning',
    message: 'Use @/constants/designSystem instead'
  },
  {
    name: 'modernRedesign',
    pattern: /@\/styles\/modernRedesign/,
    severity: 'warning',
    message: 'Use @/constants/designSystem instead'
  },
  {
    name: 'airyColors',
    pattern: /@\/constants\/airyColors/,
    severity: 'warning',
    message: 'Use @/constants/designSystem instead'
  },
  {
    name: 'Colors (old)',
    pattern: /from\s+['"]@\/constants\/Colors['"]/,
    severity: 'warning',
    message: 'Use @/constants/designSystem instead'
  },
  {
    name: 'modernMattePalette (direct)',
    pattern: /@\/constants\/modernMattePalette/,
    severity: 'info',
    message: 'Should only be imported by designSystem.ts'
  }
];

//    
const CORRECT_PATTERNS = [
  /@\/constants\/designSystem/,
  /DESIGN_TOKENS/
];

const issues = [];
const stats = {
  filesScanned: 0,
  filesWithIssues: 0,
  totalIssues: 0,
  warnings: 0,
  errors: 0,
  info: 0,
};

function checkFile(filePath) {
  try {
    const content = fs.readFileSync(filePath, 'utf8');
    const fileIssues = [];

    //    
    DEPRECATED_PATTERNS.forEach(({ name, pattern, severity, message }) => {
      const matches = content.match(pattern);
      if (matches) {
        const lines = content.split('\n');
        lines.forEach((line, index) => {
          if (pattern.test(line)) {
            fileIssues.push({
              file: filePath,
              line: index + 1,
              severity,
              pattern: name,
              message,
              code: line.trim()
            });
            stats[severity]++;
          }
        });
      }
    });

    if (fileIssues.length > 0) {
      issues.push(...fileIssues);
      stats.filesWithIssues++;
      stats.totalIssues += fileIssues.length;
    }

    stats.filesScanned++;
  } catch (error) {
    console.error(`Error reading ${filePath}:`, error.message);
  }
}

function walkDirectory(dir, extensions = ['.tsx', '.ts']) {
  const files = fs.readdirSync(dir);

  files.forEach(file => {
    const filePath = path.join(dir, file);
    const stat = fs.statSync(filePath);

    if (stat.isDirectory()) {
      //  node_modules, dist  ..
      if (!['node_modules', 'dist', 'build', '.next', '.expo', '__tests__'].includes(file)) {
        walkDirectory(filePath, extensions);
      }
    } else if (stat.isFile()) {
      const ext = path.extname(file);
      if (extensions.includes(ext)) {
        checkFile(filePath);
      }
    }
  });
}

function formatIssue(issue) {
  const severityIcons = {
    error: '',
    warning: '',
    info: ''
  };

  const icon = severityIcons[issue.severity] || '';
  const relPath = path.relative(process.cwd(), issue.file);

  return `${icon} ${relPath}:${issue.line}
   Pattern: ${issue.pattern}
   Message: ${issue.message}
   Code: ${issue.code}`;
}

function generateReport() {
  console.log('\n' + '='.repeat(80));
  console.log(' Design System Verification Report');
  console.log('='.repeat(80));

  if (issues.length === 0) {
    console.log('\n No deprecated design system usage found!');
    console.log(`   Scanned ${stats.filesScanned} files.`);
  } else {
    console.log(`\n Summary:`);
    console.log(`   Files scanned:      ${stats.filesScanned}`);
    console.log(`   Files with issues:  ${stats.filesWithIssues}`);
    console.log(`   Total issues:       ${stats.totalIssues}`);
    console.log(`   - Errors:           ${stats.errors}`);
    console.log(`   - Warnings:         ${stats.warnings}`);
    console.log(`   - Info:             ${stats.info}`);

    //   
    const issuesByFile = {};
    issues.forEach(issue => {
      if (!issuesByFile[issue.file]) {
        issuesByFile[issue.file] = [];
      }
      issuesByFile[issue.file].push(issue);
    });

    console.log('\n Issues by file:\n');
    Object.entries(issuesByFile).forEach(([file, fileIssues]) => {
      console.log(`\n${path.relative(process.cwd(), file)} (${fileIssues.length} issues):`);
      fileIssues.forEach(issue => {
        console.log(`  ${formatIssue(issue)}\n`);
      });
    });

    console.log('\n Recommended action:');
    console.log('   Run: node scripts/migrate-design-imports.js --dry-run');
    console.log('   Then: node scripts/migrate-design-imports.js');
  }

  console.log('\n' + '='.repeat(80));

  // Exit code  CI/CD
  const hasErrors = stats.errors > 0;
  if (hasErrors) {
    console.log('\n Verification failed: Errors found');
    process.exit(1);
  } else if (stats.warnings > 0) {
    console.log('\n  Verification passed with warnings');
    process.exit(0);
  } else {
    console.log('\n Verification passed');
    process.exit(0);
  }
}

function main() {
  console.log(' Scanning for deprecated design system usage...\n');

  const componentsDir = path.join(__dirname, '../components');
  const appDir = path.join(__dirname, '../app');
  const hooksDir = path.join(__dirname, '../hooks');

  walkDirectory(componentsDir);
  walkDirectory(appDir);
  walkDirectory(hooksDir);

  generateReport();
}

if (require.main === module) {
  main();
}

module.exports = { checkFile, walkDirectory };

const http = require('http');
const https = require('https');
const fs = require('fs');
const path = require('path');

const PORT = 3001;
const PUBLIC_DIR = path.join(__dirname, 'dist/prod');

const mimeTypes = {
  '.html': 'text/html',
  '.js': 'text/javascript',
  '.css': 'text/css',
  '.json': 'application/json',
  '.png': 'image/png',
  '.jpg': 'image/jpeg',
  '.gif': 'image/gif',
  '.svg': 'image/svg+xml',
  '.ico': 'image/x-icon',
};

const server = http.createServer((request, response) => {
  // Handle API proxying
  if (request.url.startsWith('/api/')) {
    const options = {
      hostname: 'metravel.by',
      port: 443,
      path: request.url,
      method: request.method,
      headers: {
        ...request.headers,
        host: 'metravel.by',
        referer: 'https://metravel.by'
      }
    };

    const proxyReq = https.request(options, (proxyRes) => {
      response.writeHead(proxyRes.statusCode, proxyRes.headers);
      proxyRes.pipe(response);
    });

    proxyReq.on('error', (e) => {
      console.error(`API Proxy Error: ${e.message}`);
      response.writeHead(502);
      response.end('Bad Gateway');
    });

    request.pipe(proxyReq);
    return;
  }

  // Safe path handling
  const safePath = path.normalize(request.url.split('?')[0]).replace(/^(\.\.[\/\\])+/, '');
  let filePath = path.join(PUBLIC_DIR, safePath);

  // If URL ends with /, try index.html
  if (request.url.endsWith('/')) {
      filePath = path.join(filePath, 'index.html');
  }

  const extname = String(path.extname(filePath)).toLowerCase();

  // Attempt to read file
  fs.readFile(filePath, (error, content) => {
    if (error) {
      if (error.code === 'ENOENT') {
        // File not found. 
        // If it looks like a static asset (has extension), return 404.
        // Otherwise, assume it's a client-side route and serve index.html (SPA fallback).
        if (extname && extname !== '.html') {
            response.writeHead(404);
            response.end('Not found');
        } else {
            // SPA Fallback
            fs.readFile(path.join(PUBLIC_DIR, 'index.html'), (err, indexContent) => {
                if (err) {
                    response.writeHead(500);
                    response.end('Error loading index.html');
                } else {
                    response.writeHead(200, { 'Content-Type': 'text/html' });
                    response.end(indexContent, 'utf-8');
                }
            });
        }
      } else {
        response.writeHead(500);
        response.end('Server Error: ' + error.code);
      }
    } else {
      const contentType = mimeTypes[extname] || 'application/octet-stream';
      response.writeHead(200, { 'Content-Type': contentType });
      response.end(content, 'utf-8');
    }
  });
});

server.listen(PORT, () => {
  console.log(`SPA Server with API Proxy running at http://localhost:${PORT}/`);
});

export const registerServiceWorker = async (): Promise<boolean> => {
  if (typeof window === 'undefined') return false;
  if (!('serviceWorker' in navigator)) return false;

  const isProd = window.location.hostname === 'metravel.by' || window.location.hostname === 'www.metravel.by';
  if (!isProd) return false;

  try {
    const registration = await navigator.serviceWorker.register('/sw.js', {
      scope: '/',
    });

    registration.addEventListener('updatefound', () => {
      const newWorker = registration.installing;
      if (!newWorker) return;

      newWorker.addEventListener('statechange', () => {
        if (newWorker.state === 'installed' && navigator.serviceWorker.controller) {
          console.log('New service worker available, page will refresh on next visit');
        }
      });
    });

    return true;
  } catch (error) {
    console.error('Service Worker registration failed:', error);
    return false;
  }
};

export const unregisterServiceWorker = async (): Promise<boolean> => {
  if (typeof window === 'undefined') return false;
  if (!('serviceWorker' in navigator)) return false;

  try {
    const registration = await navigator.serviceWorker.getRegistration();
    if (registration) {
      await registration.unregister();
      return true;
    }
    return false;
  } catch (error) {
    console.error('Service Worker unregistration failed:', error);
    return false;
  }
};
